script:
import { Component, ElementRef, ViewChild } from '@angular/core';

import '@htmlplus/ui/accordion.js';
import '@htmlplus/ui/faker.js';
import '@htmlplus/ui/progress-bar.js';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  @ViewChild('accordion')
  accordionRef!: ElementRef;
  hidden = true;
  onPlusExpand(event) {
    event.preventDefault();
    this.hidden = false;
    setTimeout(() => {
      this.hidden = true;
      this.accordionRef.nativeElement.open = true;
    }, 2500);
  }
}

import '@htmlplus/ui/accordion.js';
import '@htmlplus/ui/faker.js';
import '@htmlplus/ui/progress-bar.js';

$accordion.addEventListener('plus-expand', (event) => {
  event.preventDefault();
  $progress.hidden = false;
  setTimeout(() => {
    $progress.hidden = true;
    $accordion.open = true;
  }, 2500);
});

import { useEffect, useRef, useState } from 'react';

import '@htmlplus/ui/accordion.js';
import '@htmlplus/ui/faker.js';
import '@htmlplus/ui/progress-bar.js';

function App() {
  const accordionRef = useRef();
  const [hidden, setHidden] = useState(true);
  useEffect(() => {
    if (!accordionRef.current) return;
    function onPlusExpand(event) {
      event.preventDefault();
      setHidden(false);
      setTimeout(() => {
        setHidden(true);
        accordionRef.current.open = true;
      }, 2500);
    }
    accordionRef.current.addEventListener('plus-expand', onPlusExpand);
    return () => {
      if (!accordionRef.current) return;
      accordionRef.current.removeEventListener('plus-expand', onPlusExpand);
    };
  });
  return (
    <plus-accordion summary="Summary" ref={accordionRef}>
      <plus-progress-bar
        variant="indeterminate"
        slot="top"
        style={{
          height: '2px'
        }}
        hidden={hidden}
      ></plus-progress-bar>
      <plus-faker></plus-faker>
    </plus-accordion>
  );
}
export default App;

