script:
import { Component, ElementRef, ViewChild } from '@angular/core';

import '@htmlplus/ui/center.js';
import '@htmlplus/ui/cropper.js';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  @ViewChild('cropper')
  cropperRef!: ElementRef;
  onClick() {
    const image = new Image();
    image.src = this.cropperRef.nativeElement.toCanvas().toDataURL();
    const tab = open('', '_blank');
    setTimeout(() => {
      tab.document.write(image.outerHTML);
    }, 250);
  }
}

import '@htmlplus/ui/center.js';
import '@htmlplus/ui/cropper.js';

$button.addEventListener('click', () => {
  const image = new Image();
  image.src = $cropper.toCanvas().toDataURL();
  const tab = open('', '_blank');
  setTimeout(() => {
    tab.document.write(image.outerHTML);
  }, 250);
});

import { useRef } from 'react';

import '@htmlplus/ui/center.js';
import '@htmlplus/ui/cropper.js';

function App() {
  const cropperRef = useRef();
  function onClick() {
    const image = new Image();
    image.src = cropperRef.current.toCanvas().toDataURL();
    const tab = open('', '_blank');
    setTimeout(() => {
      tab.document.write(image.outerHTML);
    }, 250);
  }
  return (
    <>
      <plus-center>
        <plus-cropper
          src="https://fengyuanchen.github.io/cropperjs/images/picture.jpg"
          ref={cropperRef}
        ></plus-cropper>
      </plus-center>
      <br />
      <plus-center>
        <button onClick={onClick}>To Base64</button>
      </plus-center>
    </>
  );
}
export default App;

