import { Element, State } from '@htmlplus/element';

@Element()
class IntersectionDefault {
  @State()
  intersecting = false;

  onChange(event) {
    this.intersecting = event.detail.isIntersecting;
  }

  render() {
    return (
      <div class="container">
        <plus-sticky top="1rem" z-index="1">
          <plus-center>
            <div class="status">
              {this.intersecting ? 'In Viewport' : 'Out of Viewport'}
            </div>
          </plus-center>
        </plus-sticky>
        <plus-intersection onPlusChange={(event) => this.onChange(event)}>
          <plus-card elevation="10">
            <plus-card-body>
              <plus-faker></plus-faker>
            </plus-card-body>
          </plus-card>
        </plus-intersection>
      </div>
    );
  }
}

intersection1.addEventListener('plus-change', (event) => {
  status.innerHTML = event.detail.isIntersecting
    ? 'In Viewport'
    : 'Out of Viewport';
});

output.script:
import { useState } from 'react';
import { Card, Center, Faker, Intersection, Sticky } from '@htmlplus/react';
const IntersectionDefault = () => {
  const [intersecting, setIntersecting] = useState(false);
  const onChange = (event) => {
    setIntersecting(event.detail.isIntersecting);
  };
  return (
    <div className="container">
      <Sticky top="1rem" zIndex="1">
        <Center>
          <div className="status">
            {intersecting ? 'In Viewport' : 'Out of Viewport'}
          </div>
        </Center>
      </Sticky>
      <Intersection onChange={(event) => onChange(event)}>
        <Card elevation="10">
          <Card.Body>
            <Faker></Faker>
          </Card.Body>
        </Card>
      </Intersection>
    </div>
  );
};
export default IntersectionDefault;

