    this.canRedo = window.signature2.canRedo();
  }

  onUndo() {
    window.signature2.undo();
    this.sync();
  }

  onRedo() {
    window.signature2.redo();
    this.sync();
  }

  onPlusEnd() {
    this.sync();
  }

