    setCanUndo(window.signature2.canUndo());
    setCanRedo(window.signature2.canRedo());
  };
  const onUndo = () => {
    window.signature2.undo();
    sync();
  };
  const onRedo = () => {
    window.signature2.redo();
    sync();
  };
  const onPlusEnd = () => {
    sync();
  };
  return (
    <>
      <Center>
        <Signature
          id="signature2"
          backgroundColor="lightgray"
          onEnd={() => onPlusEnd()}
