script:
import { Component, ElementRef, ViewChild } from '@angular/core';

import '@htmlplus/core/accordion.js';
import '@htmlplus/core/faker.js';
import '@htmlplus/core/progress-bar.js';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  @ViewChild('accordion')
  accordionRef!: ElementRef;
  hidden = true;
  onPlusExpand(event) {
    event.preventDefault();
    this.hidden = false;
    setTimeout(() => {
      this.hidden = true;
      this.accordionRef.nativeElement.open = true;
    }, 2500);
  }
}

import '@htmlplus/core/accordion.js';
import '@htmlplus/core/faker.js';
import '@htmlplus/core/progress-bar.js';

$accordion.addEventListener('plus-expand', (event) => {
  event.preventDefault();
  $progress.hidden = false;
  setTimeout(() => {
    $progress.hidden = true;
    $accordion.open = true;
  }, 2500);
});

import { useRef, useState } from 'react';

import { Accordion, Faker, ProgressBar } from '@htmlplus/react';

function App() {
  const accordionRef = useRef();
  const [hidden, setHidden] = useState(true);
  function onExpand(event) {
    event.preventDefault();
    setHidden(false);
    setTimeout(() => {
      setHidden(true);
      accordionRef.current.open = true;
    }, 2500);
  }
  return (
    <Accordion summary="Summary" onExpand={onExpand} ref={accordionRef}>
      <ProgressBar
        variant="indeterminate"
        slot="top"
        style={{
          height: '2px'
        }}
        hidden={hidden}
      ></ProgressBar>
      <Faker></Faker>
    </Accordion>
  );
}
export default App;

