import { Component, ElementRef, ViewChild } from '@angular/core';

import '@htmlplus/core/card-body.js';
import '@htmlplus/core/card.js';
import '@htmlplus/core/click-outside.js';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  @ViewChild('outside')
  outsideRef!: ElementRef;
  @ViewChild('inside')
  insideRef!: ElementRef;
  inside = 0;
  outside = 0;
  onClick() {
    const count = this.inside + 1;
    this.inside = count;
    this.insideRef.nativeElement.innerHTML = `${count}`;
  }
  onPlusClickOutside() {
    const count = this.outside + 1;
    this.outside = count;
    this.outsideRef.nativeElement.innerHTML = `${count}`;
  }
}

import '@htmlplus/core/card-body.js';
import '@htmlplus/core/card.js';
import '@htmlplus/core/click-outside.js';

let inside = 0;
let outside = 0;
$clickOutside.addEventListener('click', () => {
  const count = inside + 1;
  inside = count;
  $inside.innerHTML = `${count}`;
});
$clickOutside.addEventListener('plus-click-outside', () => {
  const count = outside + 1;
  outside = count;
  $outside.innerHTML = `${count}`;
});

import { useRef, useState } from 'react';

import { Card, ClickOutside } from '@htmlplus/react';

function App() {
  const outsideRef = useRef();
  const insideRef = useRef();
  const [inside, setInside] = useState(0);
  const [outside, setOutside] = useState(0);
  function onClick() {
    const count = inside + 1;
    setInside(count);
    insideRef.current.innerHTML = `${count}`;
  }
  function onClickOutside() {
    const count = outside + 1;
    setOutside(count);
    outsideRef.current.innerHTML = `${count}`;
  }
  return (
    <ClickOutside onClick={onClick} onClickOutside={onClickOutside}>
      <Card elevation={10}>
        <Card.Body>
          <b ref={insideRef}>0</b> time(s) inside clicked
          <br />
          <b ref={outsideRef}>0</b> time(s) outside clicked
        </Card.Body>
      </Card>
    </ClickOutside>
  );
}
export default App;

