  @ViewChild('signature')
  signatureRef!: ElementRef;
  disabled2 = true;
  disabled1 = true;
  sync() {
    this.disabled1 = !this.signatureRef.nativeElement.canUndo();
    this.disabled2 = !this.signatureRef.nativeElement.canRedo();
  }
  onClick1() {
    this.signatureRef.nativeElement.undo();
    this.sync();
